import {
    createDefaultModule, createDefaultSharedModule, DefaultSharedModuleContext, inject,
    LangiumServices, LangiumSharedServices, Module, PartialLangiumServices
} from 'langium';
import { PolyArduinoGeneratedModule, PolyArduinoGeneratedSharedModule } from './generated/module';
import { PolyArduinoValidationRegistry, PolyArduinoValidator } from './poly-arduino-validator';

/**
 * Declaration of custom services - add your own service classes here.
 */
export type PolyArduinoAddedServices = {
    validation: {
        PolyArduinoValidator: PolyArduinoValidator
    }
}

/**
 * Union of Langium default services and your custom services - use this as constructor parameter
 * of custom service classes.
 */
export type PolyArduinoServices = LangiumServices & PolyArduinoAddedServices

/**
 * Dependency injection module that overrides Langium default services and contributes the
 * declared custom services. The Langium defaults can be partially specified to override only
 * selected services, while the custom services must be fully specified.
 */
export const PolyArduinoModule: Module<PolyArduinoServices, PartialLangiumServices & PolyArduinoAddedServices> = {
    validation: {
        ValidationRegistry: (services) => new PolyArduinoValidationRegistry(services),
        PolyArduinoValidator: () => new PolyArduinoValidator()
    }
};

/**
 * Create the full set of services required by Langium.
 *
 * First inject the shared services by merging two modules:
 *  - Langium default shared services
 *  - Services generated by langium-cli
 *
 * Then inject the language-specific services by merging three modules:
 *  - Langium default language-specific services
 *  - Services generated by langium-cli
 *  - Services specified in this file
 *
 * @param context Optional module context with the LSP connection
 * @returns An object wrapping the shared services and the language-specific services
 */
export function createPolyArduinoServices(context: DefaultSharedModuleContext): {
    shared: LangiumSharedServices,
    PolyArduino: PolyArduinoServices
} {
    const shared = inject(
        createDefaultSharedModule(context),
        PolyArduinoGeneratedSharedModule
    );
    const PolyArduino = inject(
        createDefaultModule({ shared }),
        PolyArduinoGeneratedModule,
        PolyArduinoModule
    );
    shared.ServiceRegistry.register(PolyArduino);
    return { shared, PolyArduino };
}
